mapper.select:
SELECT * FROM GOODS G JOIN CART C ON G.GOODS_CD = C.GOODS_CD AND G.GOODS_CD IN
#{goodsCd}
ORDER BY C.ENROLL_DT DESC

mapper.update:
UPDATE
		GOODS
SET

		XS_STOCK = XS_STOCK - #{orderGoodsQty}


		SM_STOCK = SM_STOCK - #{orderGoodsQty}


		MD_STOCK = MD_STOCK - #{orderGoodsQty}


		LG_STOCK = LG_STOCK - #{orderGoodsQty}


		XL_STOCK = XL_STOCK - #{orderGoodsQty}


WHERE
		GOODS_CD = #{goodsCd}

UPDATE
		MEMBER
SET
		POINT = POINT + #{point}
WHERE
		MEMBER_ID = #{memberId}
